mapper.select:
select b_no, m_id, m_nick, c_no, c_dist, b_title, b_content, b_date, b_updatedate, b_hit, b_replycnt, b_lovecnt, b_hatecnt, b_reportcnt from ( select /*+ INDEX_DESC(board pk_board)*/ rownum rn, b_no, b.m_id, m.m_nick, c.c_dist c_dist, b.c_no, b_title, b_content, b_date, b_updatedate, b_hit, b_replycnt, b_lovecnt, b_hatecnt, b_reportcnt from (select *from board order by b_no desc) b, category c, member m where rownum <= #{pageNum} * #{amount} and b.c_no=c.c_no and m.m_id = b.m_id
and b.c_no>10000 and b.c_no<10010
and b.c_no<10000
and b.c_no=#{categoryNum}
and b_reportcnt >= 5
and (b_title like '%'||#{keyword}||'%' or b_content like '%'||#{keyword}||'%' or b.m_id like '%'||#{keyword}||'%')
and b.${searchField} like '%'||#{keyword}||'%'
and (b_title like '%'||#{keyword}||'%' or b_content like '%'||#{keyword}||'%')
and m.${searchField} like '%'||#{keyword}||'%'
) where rn > (#{pageNum}-1) * #{amount}
