mapper.select:

  		select g_no, gc_code, m_id, g_title, g_content, g_price, g_date, g_updatedate, g_replycnt, c_no
  		from
  			(
  				select rownum rn, g_no, gc_code, m_id, g_title, g_content, g_price, g_date, g_updatedate, g_replycnt, c_no
  				from goods
  				where rownum<=#{pageNum} * #{amount}
  			)
		where rn > (#{pageNum}-1) * #{amount} and c_no=#{categoryNum}
  			)
