mapper.select:
select g_no, gc_code, m_id, g_title, g_content, g_price, g_date, g_updatedate, g_replycnt, c_no, m_nick from ( select /*+ INDEX_DESC(goods pk_goods)*/ rownum rn, g_no, gc_code, g.m_id, g_title, g_content, g_price, g_date, g_updatedate, g_replycnt, c_no, m_nick from (select * from goods order by g_updatedate desc) g, member m where rownum <= #{pageNum} * #{amount}
and gc_code = #{gc_code}
and gc_code > 0
and g.c_no in (select g.c_no from category c, goods g where c.c_no=g.c_no and c_dist=#{keyword})
and m.m_id=g.m_id
) where rn > (#{pageNum}-1) * #{amount}


select g_no, gc_code, m_id, g_title, g_content, g_price, g_date, g_updatedate, g_replycnt, c_no, g_reportcnt
	from
		(
			select
				rownum rn, g_no, gc_code, m_id, g_title, g_content, g_price, g_date, g_updatedate, g_replycnt, c_no, g_reportcnt
			from (select * from goods order by g_date desc)
			where g_reportcnt >= 5 and rownum <= #{pageNum} * #{amount} 
		)
	where rn > (#{pageNum}-1) * #{amount}


	select rownum rn, g_no, gc_code, m_id, m_nick, c_no, g_title, g_date, g_updatedate, g_content
 	 from (select g_no, gc_code, g.m_id, m.m_nick, c_no, g_title, g_date, g_updatedate, g_content from goods g, member m where m.m_id = g.m_id and c_no > 1001 and c_no < 1100 order by g_no desc) 
      where rownum <= 3
