mapper.select:

select
	g_no, gc_code, m_id, g_title, g_content, g_price, g_date, g_updatedate, g_replycnt, c_no
from
	(
		select /*+ INDEX_DESC(goods pk_goods)*/
			rownum rn, g_no, gc_code, m_id, g_title, g_content, g_price, g_date, g_updatedate, g_replycnt, c_no
		from goods
		where rownum <= #{pageNum} * #{amount}




				and gc_code = #{gc_code}


				and gc_code > 0



			 and goods.c_no=(select goods.c_no from category c where c.c_no=goods.c_no and c_dist=#{keyword})






	)
where rn > (#{pageNum}-1) * #{amount}

mapper.update:
	update goods set

          g_replycnt = (select count(*) from goodsReply where g_no = #{g_no})

	where g_no = #{g_no}
