mapper.insert:

		select seq_goodsReply.nextval from dual

	insert into goodsReply(gr_no, g_no, m_id, gr_content,gr_ref,gr_refid,gr_level,gr_step,gr_reCnt,gr_state)
  values(#{gr_no}, #{g_no}, #{m_id}, #{gr_content},


   		#{gr_no},#{m_id}


    		#{gr_ref},#{gr_refid}


    ,#{gr_level},#{gr_step},#{gr_reCnt},#{gr_state})
